import React, { Component } from 'react';
import Modal from "./components/Modal";
import Slider from './components/Slider';
import './App.scss';

class App extends Component {
  state = {
    show: false
  };
  showModal = e => {
    this.setState({
      show: !this.state.show
    });
  };

  render() {
    return (
      <div className="App">
      <Slider />
      <button
          className="toggle-button"
          id="centered-toggle-button"
          onClick={e => {
            this.showModal(e);
          }}
        >
          {" "}
          show Modal{" "}
        </button>

